<?php//baga un feed rss intr-un arrayfunction rss_to_array($feed) {    $feed_to_array = (array) new SimpleXmlElement(file_get_contents($feed));    return $feed_to_array;}function is_page($the_page) {    $whole_page = $_SERVER['PHP_SELF'];    $whole_page_pieces = explode("/", $whole_page);    $page_php = end($whole_page_pieces);    $page_php_pieces = explode(".", $page_php);    $page = $page_php_pieces[0];    if ($the_page == $page) {        return true;    } else {        return false;    }}function login() {    $email = $_POST["log_email"];    $pass = $_POST["log_password"];    if (check_login($email, $pass)) {        $_SESSION['loggedin'] = 'true';        $_SESSION['logged_email'] = $email;        echo '<section class="warn success"><span class="success">autentificare reusita</span></section>';    } else {        echo '<section class="warn unsuccess"><span class="unsuccess">autentificare nereusita. Mai incearca odata <a href="#log-in">aici</a></span></section>';    }    session_write_close();}function is_loggedin() {    if ($_SESSION['logged'] == "ok") {        return true;    } else {        return false;    }}function logout() {    unset($_SESSION['logged']);    unset($_SESSION['logged_email']);    echo '<section class="warn success"><span class="success">Ai fost deautentificat cu succes! </span></section>';}function the_excerpt($content) {    echo strip_tags(substr($content, 0, 200), '<img>');}?>